package icai.dtc.isw.dao;

import icai.dtc.isw.domain.Anuncio;

import java.sql.*;
import java.util.ArrayList;
import java.util.List;

public class AnuncioDAO {

    /**
     * Inserta un nuevo anuncio en la base de datos.
     * Los campos creado_en y actualizado_en se establecen a NOW().
     */
    public boolean insert(Anuncio anuncio) {
        Connection con = ConnectionDAO.getInstance().getConnection();
        String sql = """
            INSERT INTO anuncios (id, descripcion, precio, categoria, especificacion, ubicacion, nif_empresa, creado_en, actualizado_en)
            VALUES (?, ?, ?, ?, ?, ?, ?, NOW(), NOW())
            """;
        try (PreparedStatement pst = con.prepareStatement(sql)) {
            pst.setString(1, anuncio.getId());
            pst.setString(2, anuncio.getDescripcion());
            pst.setDouble(3, anuncio.getPrecio());
            pst.setString(4, anuncio.getCategoria());
            pst.setString(5, anuncio.getEspecificacion());
            pst.setString(6, anuncio.getUbicacion());
            pst.setString(7, anuncio.getNifEmpresa());

            int rows = pst.executeUpdate();
            return rows > 0;
        } catch (SQLException ex) {
            ex.printStackTrace();
            return false;
        }
    }

    /**
     * Obtiene un anuncio por su ID.
     */
    public Anuncio findById(String id) {
        if (id == null || id.isBlank()) return null;
        Connection con = ConnectionDAO.getInstance().getConnection();
        String sql = "SELECT id, descripcion, precio, categoria, especificacion, ubicacion, nif_empresa, creado_en, actualizado_en FROM anuncios WHERE id = ?";

        try (PreparedStatement pst = con.prepareStatement(sql)) {
            pst.setString(1, id);
            try (ResultSet rs = pst.executeQuery()) {
                if (rs.next()) {
                    return mapResultSetToAnuncio(rs);
                }
            }
        } catch (SQLException ex) {
            ex.printStackTrace();
        }
        return null;
    }

    /**
     * Obtiene todos los anuncios de una empresa (por NIF).
     */
    public List<Anuncio> findByNifEmpresa(String nifEmpresa) {
        List<Anuncio> anuncios = new ArrayList<>();
        if (nifEmpresa == null || nifEmpresa.isBlank()) return anuncios;

        Connection con = ConnectionDAO.getInstance().getConnection();
        String sql = "SELECT id, descripcion, precio, categoria, especificacion, ubicacion, nif_empresa, creado_en, actualizado_en FROM anuncios WHERE nif_empresa = ? ORDER BY creado_en DESC";

        try (PreparedStatement pst = con.prepareStatement(sql)) {
            pst.setString(1, nifEmpresa);
            try (ResultSet rs = pst.executeQuery()) {
                while (rs.next()) {
                    anuncios.add(mapResultSetToAnuncio(rs));
                }
            }
        } catch (SQLException ex) {
            ex.printStackTrace();
        }
        return anuncios;
    }

    /**
     * Obtiene todos los anuncios.
     */
    public List<Anuncio> findAll() {
        List<Anuncio> anuncios = new ArrayList<>();
        Connection con = ConnectionDAO.getInstance().getConnection();
        String sql = "SELECT id, descripcion, precio, categoria, especificacion, ubicacion, nif_empresa, creado_en, actualizado_en FROM anuncios ORDER BY creado_en DESC";

        try (Statement st = con.createStatement();
             ResultSet rs = st.executeQuery(sql)) {
            while (rs.next()) {
                anuncios.add(mapResultSetToAnuncio(rs));
            }
        } catch (SQLException ex) {
            ex.printStackTrace();
        }
        return anuncios;
    }

    /**
     * Actualiza un anuncio existente.
     * El campo actualizado_en se establece a NOW().
     */
    public boolean update(Anuncio anuncio) {
        Connection con = ConnectionDAO.getInstance().getConnection();
        String sql = """
            UPDATE anuncios 
            SET descripcion = ?, precio = ?, categoria = ?, especificacion = ?, 
                ubicacion = ?, actualizado_en = NOW()
            WHERE id = ?
            """;
        try (PreparedStatement pst = con.prepareStatement(sql)) {
            pst.setString(1, anuncio.getDescripcion());
            pst.setDouble(2, anuncio.getPrecio());
            pst.setString(3, anuncio.getCategoria());
            pst.setString(4, anuncio.getEspecificacion());
            pst.setString(5, anuncio.getUbicacion());
            pst.setString(6, anuncio.getId());

            int rows = pst.executeUpdate();
            return rows > 0;
        } catch (SQLException ex) {
            ex.printStackTrace();
            return false;
        }
    }

    /**
     * Elimina un anuncio por su ID.
     */
    public boolean delete(String id) {
        if (id == null || id.isBlank()) return false;
        Connection con = ConnectionDAO.getInstance().getConnection();
        String sql = "DELETE FROM anuncios WHERE id = ?";

        try (PreparedStatement pst = con.prepareStatement(sql)) {
            pst.setString(1, id);
            int rows = pst.executeUpdate();
            return rows > 0;
        } catch (SQLException ex) {
            ex.printStackTrace();
            return false;
        }
    }

    /**
     * Mapea un ResultSet a un objeto Anuncio.
     */
    private Anuncio mapResultSetToAnuncio(ResultSet rs) throws SQLException {
        Anuncio a = new Anuncio();
        a.setId(rs.getString("id"));
        a.setDescripcion(rs.getString("descripcion"));
        a.setPrecio(rs.getDouble("precio"));
        a.setCategoria(rs.getString("categoria"));
        a.setEspecificacion(rs.getString("especificacion"));
        a.setUbicacion(rs.getString("ubicacion"));
        a.setNifEmpresa(rs.getString("nif_empresa"));
        a.setCreadoEn(rs.getTimestamp("creado_en"));
        a.setActualizadoEn(rs.getTimestamp("actualizado_en"));
        return a;
    }
}

package icai.dtc.isw.dao;

import icai.dtc.isw.domain.Anuncio;

import java.sql.*;
import java.util.ArrayList;
import java.util.List;

public class AnuncioDAO {

    /**
     * Inserta un nuevo anuncio en la base de datos.
     * Los campos creado_en y actualizado_en se establecen a NOW().
     */
    public boolean insert(Anuncio anuncio) {
        Connection con = ConnectionDAO.getInstance().getConnection();
        String sql = """
            INSERT INTO anuncios (id, descripcion, precio, categoria, especificacion, ubicacion, nif_empresa, creado_en, actualizado_en)
            VALUES (?, ?, ?, ?, ?, ?, ?, NOW(), NOW())
            """;
        try (PreparedStatement pst = con.prepareStatement(sql)) {
            pst.setString(1, anuncio.getId());
            pst.setString(2, anuncio.getDescripcion());
            pst.setDouble(3, anuncio.getPrecio());
            pst.setString(4, anuncio.getCategoria());
            pst.setString(5, anuncio.getEspecificacion());
            pst.setString(6, anuncio.getUbicacion());
            pst.setString(7, anuncio.getNifEmpresa());

            int rows = pst.executeUpdate();
            return rows > 0;
        } catch (SQLException ex) {
            ex.printStackTrace();
            return false;
        }
    }

    /**
     * Obtiene un anuncio por su ID.
     */
    public Anuncio findById(String id) {
        if (id == null || id.isBlank()) return null;
        Connection con = ConnectionDAO.getInstance().getConnection();
        String sql = "SELECT id, descripcion, precio, categoria, especificacion, ubicacion, nif_empresa, creado_en, actualizado_en FROM anuncios WHERE id = ?";

        try (PreparedStatement pst = con.prepareStatement(sql)) {
            pst.setString(1, id);
            try (ResultSet rs = pst.executeQuery()) {
                if (rs.next()) {
                    return mapResultSetToAnuncio(rs);
                }
            }
        } catch (SQLException ex) {
            ex.printStackTrace();
        }
        return null;
    }

    /**
     * Obtiene todos los anuncios de una empresa (por NIF).
     */
    public List<Anuncio> findByNifEmpresa(String nifEmpresa) {
        List<Anuncio> anuncios = new ArrayList<>();
        if (nifEmpresa == null || nifEmpresa.isBlank()) return anuncios;

        Connection con = ConnectionDAO.getInstance().getConnection();
        String sql = "SELECT id, descripcion, precio, categoria, especificacion, ubicacion, nif_empresa, creado_en, actualizado_en FROM anuncios WHERE nif_empresa = ? ORDER BY creado_en DESC";

        try (PreparedStatement pst = con.prepareStatement(sql)) {
            pst.setString(1, nifEmpresa);
            try (ResultSet rs = pst.executeQuery()) {
                while (rs.next()) {
                    anuncios.add(mapResultSetToAnuncio(rs));
                }
            }
        } catch (SQLException ex) {
            ex.printStackTrace();
        }
        return anuncios;
    }

    /**
     * Obtiene todos los anuncios.
     */
    public List<Anuncio> findAll() {
        List<Anuncio> anuncios = new ArrayList<>();
        Connection con = ConnectionDAO.getInstance().getConnection();
        String sql = "SELECT id, descripcion, precio, categoria, especificacion, ubicacion, nif_empresa, creado_en, actualizado_en FROM anuncios ORDER BY creado_en DESC";

        try (Statement st = con.createStatement();
             ResultSet rs = st.executeQuery(sql)) {
            while (rs.next()) {
                anuncios.add(mapResultSetToAnuncio(rs));
            }
        } catch (SQLException ex) {
            ex.printStackTrace();
        }
        return anuncios;
    }

    /**
     * Actualiza un anuncio existente.
     * El campo actualizado_en se establece a NOW().
     */
    public boolean update(Anuncio anuncio) {
        Connection con = ConnectionDAO.getInstance().getConnection();
        String sql = """
            UPDATE anuncios 
            SET descripcion = ?, precio = ?, categoria = ?, especificacion = ?, 
                ubicacion = ?, actualizado_en = NOW()
            WHERE id = ?
            """;
        try (PreparedStatement pst = con.prepareStatement(sql)) {
            pst.setString(1, anuncio.getDescripcion());
            pst.setDouble(2, anuncio.getPrecio());
            pst.setString(3, anuncio.getCategoria());
            pst.setString(4, anuncio.getEspecificacion());
            pst.setString(5, anuncio.getUbicacion());
            pst.setString(6, anuncio.getId());

            int rows = pst.executeUpdate();
            System.out.println("AnuncioDAO.update: Filas actualizadas = " + rows + " para ID = " + anuncio.getId());
            return rows > 0;
        } catch (SQLException ex) {
            System.err.println("AnuncioDAO.update: Error SQL al actualizar anuncio ID = " + anuncio.getId());
            ex.printStackTrace();
            return false;
        }
    }

    /**
     * Elimina un anuncio por su ID.
     */
    public boolean delete(String id) {
        if (id == null || id.isBlank()) {
            System.out.println("AnuncioDAO.delete: ID es null o vacío");
            return false;
        }
        Connection con = ConnectionDAO.getInstance().getConnection();
        String sql = "DELETE FROM anuncios WHERE id = ?";

        try (PreparedStatement pst = con.prepareStatement(sql)) {
            pst.setString(1, id);
            int rows = pst.executeUpdate();
            System.out.println("AnuncioDAO.delete: Filas eliminadas = " + rows + " para ID = " + id);
            return rows > 0;
        } catch (SQLException ex) {
            System.err.println("AnuncioDAO.delete: Error SQL al eliminar anuncio ID = " + id);
            ex.printStackTrace();
            return false;
        }
    }

    /**
     * Mapea un ResultSet a un objeto Anuncio.
     */
    private Anuncio mapResultSetToAnuncio(ResultSet rs) throws SQLException {
        Anuncio a = new Anuncio();
        a.setId(rs.getString("id"));
        a.setDescripcion(rs.getString("descripcion"));
        a.setPrecio(rs.getDouble("precio"));
        a.setCategoria(rs.getString("categoria"));
        a.setEspecificacion(rs.getString("especificacion"));
        a.setUbicacion(rs.getString("ubicacion"));
        a.setNifEmpresa(rs.getString("nif_empresa"));
        a.setCreadoEn(rs.getTimestamp("creado_en"));
        a.setActualizadoEn(rs.getTimestamp("actualizado_en"));
        return a;
    }
}

package icai.dtc.isw.ui;

import icai.dtc.isw.domain.Empresa;

import javax.swing.*;
import javax.swing.border.EmptyBorder;
import java.awt.*;

public class CrearAnuncioPanel extends JDialog {

    private final AnuncioApi api = new AnuncioApi();
    private final Empresa empresa;
    private final AppMovilMock host;

    // Categorías permitidas
    private static final String[] CATEGORIAS = {
        "Hogar y reparaciones",
        "Salud, belleza y cuidados",
        "Educación y cultura",
        "Eventos y ocio",
        "Negocio y administración",
        "Logística y movilidad",
        "Tecnología y digital"
    };

    // Componentes del formulario
    private JTextArea txtDescripcion;
    private JTextField txtPrecio;
    private JComboBox<String> cboCategoria;
    private JTextField txtEspecificacion;
    private JTextField txtUbicacion;
    private JTextField txtNifEmpresa;

    public CrearAnuncioPanel(Frame parent, Empresa empresa, AppMovilMock host) {
        super(parent, "Crear Anuncio", true);
        this.empresa = empresa;
        this.host = host;

        initComponents();
        setLocationRelativeTo(parent);
    }

    private void initComponents() {
        setLayout(new BorderLayout(10, 10));
        setBackground(new Color(245, 247, 250));

        // Panel principal con el formulario
        JPanel mainPanel = new JPanel();
        mainPanel.setLayout(new BorderLayout());
        mainPanel.setBackground(Color.WHITE);
        mainPanel.setBorder(new EmptyBorder(20, 20, 20, 20));

        // Título
        JLabel titleLabel = new JLabel("Crear nuevo anuncio", SwingConstants.CENTER);
        titleLabel.setFont(new Font("SansSerif", Font.BOLD, 20));
        titleLabel.setForeground(new Color(20, 40, 80));
        titleLabel.setBorder(new EmptyBorder(0, 0, 15, 0));
        mainPanel.add(titleLabel, BorderLayout.NORTH);

        // Formulario
        JPanel formPanel = new JPanel(new GridBagLayout());
        formPanel.setBackground(Color.WHITE);
        GridBagConstraints gbc = UIUtils.baseGbc();

        // Descripción (JTextArea)
        gbc.gridy = 0;
        formPanel.add(createLabel("Descripción:"), gbc);

        txtDescripcion = new JTextArea(4, 22);
        txtDescripcion.setLineWrap(true);
        txtDescripcion.setWrapStyleWord(true);
        txtDescripcion.setBorder(BorderFactory.createCompoundBorder(
                new UIUtils.RoundedBorder(12, new Color(220, 226, 235)),
                new EmptyBorder(8, 10, 8, 10)
        ));
        txtDescripcion.setBackground(new Color(250, 251, 253));
        JScrollPane scrollDesc = new JScrollPane(txtDescripcion);
        scrollDesc.setBorder(new UIUtils.RoundedBorder(12, new Color(220, 226, 235)));
        gbc.gridy = 1;
        formPanel.add(scrollDesc, gbc);

        // Precio
        gbc.gridy = 2;
        formPanel.add(createLabel("Precio (€):"), gbc);
        txtPrecio = UIUtils.styledTextField(22);
        gbc.gridy = 3;
        formPanel.add(txtPrecio, gbc);

        // Categoría
        gbc.gridy = 4;
        formPanel.add(createLabel("Categoría:"), gbc);
        cboCategoria = UIUtils.styledCombo(CATEGORIAS);
        gbc.gridy = 5;
        formPanel.add(cboCategoria, gbc);

        // Especificación
        gbc.gridy = 6;
        formPanel.add(createLabel("Especificación:"), gbc);
        txtEspecificacion = UIUtils.styledTextField(22);
        gbc.gridy = 7;
        formPanel.add(txtEspecificacion, gbc);

        // Ubicación
        gbc.gridy = 8;
        formPanel.add(createLabel("Ubicación:"), gbc);
        txtUbicacion = UIUtils.styledTextField(22);
        gbc.gridy = 9;
        formPanel.add(txtUbicacion, gbc);

        // NIF Empresa (read-only)
        gbc.gridy = 10;
        formPanel.add(createLabel("NIF Empresa:"), gbc);
        txtNifEmpresa = UIUtils.styledTextField(22);
        txtNifEmpresa.setText(empresa != null ? empresa.getNif() : "");
        txtNifEmpresa.setEditable(false);
        txtNifEmpresa.setBackground(new Color(240, 242, 245));
        gbc.gridy = 11;
        formPanel.add(txtNifEmpresa, gbc);

        mainPanel.add(formPanel, BorderLayout.CENTER);

        // Botones
        JPanel buttonPanel = new JPanel(new FlowLayout(FlowLayout.CENTER, 10, 10));
        buttonPanel.setBackground(Color.WHITE);

        JButton btnCrear = UIUtils.primaryButton("Crear anuncio");
        btnCrear.addActionListener(e -> crearAnuncio());

        JButton btnCancelar = UIUtils.secondaryButton("Cancelar");
        btnCancelar.addActionListener(e -> dispose());

        buttonPanel.add(btnCrear);
        buttonPanel.add(btnCancelar);

        mainPanel.add(buttonPanel, BorderLayout.SOUTH);

        add(mainPanel, BorderLayout.CENTER);

        pack();
        setMinimumSize(new Dimension(500, 650));
    }

    private JLabel createLabel(String text) {
        JLabel label = new JLabel(text);
        label.setFont(new Font("SansSerif", Font.BOLD, 13));
        label.setForeground(new Color(60, 70, 90));
        return label;
    }

    private void crearAnuncio() {
        // Obtener valores
        String descripcion = txtDescripcion.getText().trim();
        String precioStr = txtPrecio.getText().trim();
        String categoria = (String) cboCategoria.getSelectedItem();
        String especificacion = txtEspecificacion.getText().trim();
        String ubicacion = txtUbicacion.getText().trim();
        String nifEmpresa = txtNifEmpresa.getText().trim();

        // Validar campos obligatorios
        if (descripcion.isEmpty()) {
            mostrarError("La descripción es obligatoria.");
            return;
        }

        if (precioStr.isEmpty()) {
            mostrarError("El precio es obligatorio.");
            return;
        }

        // Validar que el precio sea numérico
        Double precio;
        try {
            precio = Double.parseDouble(precioStr);
            if (precio <= 0) {
                mostrarError("El precio debe ser mayor que cero.");
                return;
            }
        } catch (NumberFormatException e) {
            mostrarError("El precio debe ser un número válido.");
            return;
        }

        if (categoria == null || categoria.isEmpty()) {
            mostrarError("Debes seleccionar una categoría.");
            return;
        }

        if (especificacion.isEmpty()) {
            mostrarError("La especificación es obligatoria.");
            return;
        }

        if (ubicacion.isEmpty()) {
            mostrarError("La ubicación es obligatoria.");
            return;
        }

        if (nifEmpresa.isEmpty()) {
            mostrarError("El NIF de la empresa no está disponible.");
            return;
        }

        // Intentar crear el anuncio
        try {
            boolean exito = api.createAnuncio(descripcion, precio, categoria,
                                              especificacion, ubicacion, nifEmpresa);

            if (exito) {
                JOptionPane.showMessageDialog(
                    this,
                    "Anuncio creado correctamente.",
                    "Éxito",
                    JOptionPane.INFORMATION_MESSAGE
                );

                // Cerrar el diálogo
                dispose();

                // Volver al panel de inicio o actualizar la vista
                if (host != null) {
                    // Puedes navegar a donde sea necesario, por ejemplo:
                    // host.showInicio();
                }
            } else {
                mostrarError("No se pudo crear el anuncio. Inténtalo de nuevo.");
            }
        } catch (Exception ex) {
            ex.printStackTrace();
            mostrarError("Error al crear el anuncio: " + ex.getMessage());
        }
    }

    private void mostrarError(String mensaje) {
        JOptionPane.showMessageDialog(
            this,
            mensaje,
            "Error de validación",
            JOptionPane.WARNING_MESSAGE
        );
    }
}

